import axios from "axios";
import React, { useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import url from "./url";

function InsertComponent() {
  const [status, setStatus] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleInsert = (e) => {
    e.preventDefault();
    setStatus('Loading');

    const obj = {
      p_id: parseInt(e.target.p_id.value),
      p_name: e.target.p_name.value,
    };

    axios.post(`${url}/insert`, obj)
      .then((posRes) => {
        console.log(posRes);
        setStatus(posRes.data.insert);
      })
      .catch((err) => {
        console.error(err);
      });
  };

  const handleUsernameChange = (e) => {
    setUsername(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSignIn = (e) => {
    e.preventDefault();
    setStatus('Loading');

    const credentials = {
      username: username,
      password: password,
    };

    // Make a POST request to the sign-in endpoint
    axios.post(`${url}/signin`, credentials)
      .then((res) => {
        console.log(res);
        setStatus(res.data.message);
      })
      .catch((err) => {
        console.error(err);
      });
  };

  return (
    <div className="container mt-2">
      <h1 className="text-primary">Sign Up</h1>
      <form onSubmit={handleInsert} className="w-50 mx-auto">
        <input
          type="number"
          placeholder="Enter p_id"
          name="p_id"
          className="form-control my-2"
        />
        <input
          type="text"
          placeholder="Enter p_name"
          name="p_name"
          className="form-control my-2"
        />
        <input
          type="submit"
          value="Create"
          className="btn btn-outline-success"
        />
      </form>
      <h1 className="text-info">{status}</h1>

      <h1 className="text-primary mt-5">Sign In</h1>
      <form onSubmit={handleSignIn} className="w-50 mx-auto">
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={handleUsernameChange}
          className="form-control my-2"
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={handlePasswordChange}
          className="form-control my-2"
        />
        <input
          type="submit"
          value="Sign In"
          className="btn btn-primary"
        />
      </form>
    </div>
  );
}

export default InsertComponent;
