import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

function SignupPage() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [signupStatus, setSignupStatus] = useState(null);

  const handleUsernameChange = (e) => {
    setUsername(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('mongodb://0.0.0.0:27017/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });

      if (response.ok) {
        setSignupStatus(true);
      } else {
        setSignupStatus(false);
      }
    } catch (error) {
      console.error('Error during signup:', error);
      setSignupStatus(false);
    }
  };

  return (
    <div className="container">
      <h2 className="mt-5">ECOMMERCE_SITE</h2>
      <form className="mt-4" onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="username" className="form-label">
            Username:
          </label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={handleUsernameChange}
            className="form-control"
          />
        </div>
        <div className="mb-3">
          <label htmlFor="password" className="form-label">
            Password:
          </label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={handlePasswordChange}
            className="form-control"
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Signup
        </button>
      </form>
      {signupStatus === true && <p className="mt-3 text-success">Signup successful!</p>}
      {signupStatus === false && <p className="mt-3 text-danger">Signup failed. Please check your details.</p>}
    </div>
  );
}

export default SignupPage;
