import React from 'react';
import axios from 'axios';
import url from './url';
import Header from './Header';
import 'bootstrap/dist/css/bootstrap.min.css';

let cart = [];

export default class MainCompo extends React.Component {
    constructor() {
        super();
        this.state = {
            products: [],
            status: '',
            login: false,
            user: '', // User field in the state
        };
    }

    componentDidMount() {
        // Fetch products data and user's cart
        axios.get(url + '/fetch')
            .then((posRes) => {
                console.log(posRes.data);
                this.setState({
                    status: 'Loading'
                });
                this.setState({
                    products: posRes.data,
                    status: ''
                });
            })
            .catch((errRes) => {
                console.log(errRes);
            });
        this.fetchCart();
    }

    render() {
        return (
            <div className="container-fluid">
            {/* Login Form */}
            {!this.state.login && (
                <div className="login-form">
                    <form onSubmit={this.login}>
                        <div className="mb-3">
                            <label htmlFor="username" className="form-label">Username</label>
                            <input type="text" id="username" placeholder="Enter username" className="form-control" name="u_name" required />
                        </div>
                        <div className="mb-3">
                            <label htmlFor="password" className="form-label">Password</label>
                            <input type="password" id="password" placeholder="Enter password" className="form-control" name="u_pwd" required />
                        </div>
                        <div className="text-center">
                            <button type="submit" className="btn btn-primary">Login</button>
                        </div>
                    </form>
                </div>
            )}
        

                {/* Main Content */}
                <div hidden={!this.state.login}>
                    <button onClick={this.logout} className='btn btn-outline-danger float-right'>Logout</button>
                    <Header />
                    <div className='h4 text-info mb-2' align="right">
                        Total amount:- {this.calculateTotal()}
                        <button onClick={() => { this.buyNow() }} className='btn btn-outline-success mx-5'>Buy Now</button>
                    </div>
                    <div className='row'>
                        <div className='col-10'>
                            <div className='row row-cols-3 my-3'>
                                {/* Product Cards */}
                                {this.state.products.map((product, index) => (
                                    <div className='col my-3' key={index}>
                                        <div className='card'>
                                            <div className='card-header'>
                                                <img src={product.p_img} className='card-img-top'></img>
                                            </div>
                                            <div className='card-body'>
                                                <div className='h2 card-title'>{product.p_name}</div>
                                                <div className='h4 card-subtitle text-muted'>{product.p_cost}</div>
                                            </div>
                                            <div className='card-footer'>
                                                <button onClick={() => { alert(product.p_desc) }}
                                                    className="btn btn-outline-info btn-block btn-sm"
                                                    data-toggle="tooltip"
                                                    data-placement="bottom"
                                                    title={product.p_desc}>Learn More</button>
                                                <button onClick={() => { this.addToCart(product) }} className="btn btn-outline-success btn-block btn-sm">Add to Cart</button>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>
                        <div className='col'>
                            {/* Cart Items */}
                            <div className='row my-3'>
                                {cart.map((item, index) => (
                                    <div className='my-3' key={index}>
                                        <div className='card'>
                                            <div className='card-header'>
                                                <img src={item.p_img} className='card-img-top'></img>
                                            </div>
                                            <div className='card-body'>
                                                <div className='h2 card-title'>{item.p_name}</div>
                                                <div className='h4 card-subtitle text-muted'>{item.qty}</div>
                                            </div>
                                            <div className='card-footer'>
                                                <button onClick={() => { this.reduce(item) }} className="btn btn-outline-success btn-block btn-sm">Reduce</button>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
    addToCart = (item) => {
        const existingItem = cart.find((cartItem) => cartItem.p_id === item.p_id);
    
        if (existingItem) {
            const updatedQuantity = existingItem.qty + 1;
            const updateObj = {
                u_name: this.state.user,
                p_id: existingItem.p_id,
                qty: updatedQuantity,
            };
    
            axios.post(url + "/update/updateCart", updateObj)
                .then((res) => {
                    cart.forEach((cartItem) => {
                        if (cartItem.p_id === updateObj.p_id) {
                            cartItem.qty = updatedQuantity;
                        }
                    });
                    console.log(res.statusText);
                    this.setState({ status: 'Update ' + res.statusText });
                })
                .catch((error) => {
                    console.error('Error updating cart:', error);
                    this.setState({ status: error.message });
                });
        } else {
            const newItem = {
                u_name: this.state.user,
                p_name: item.p_name,
                p_id: item.p_id,
                qty: 1,
                p_cost: item.p_cost,
                p_img: item.p_img,
            };
    
            axios.post(url + "/insert/cartInsert", newItem)
                .then((res) => {
                    this.setState({ status: 'Record ' + res.statusText });
                    cart.push(newItem);
                })
                .catch((error) => {
                    console.error('Error adding to cart:', error);
                });
        }
    
        this.setState({ total: this.calculateTotal() });
    }
    

    buyNow = () => {
        alert('Thank u for business with us Total amount :- ' + this.calculateTotal())
        for (let i = 0; i < cart.length; i++) {
            axios.delete("http://localhost:8086/cart/" + cart[i].id)
                .then((posRes) => {
                    console.log(posRes)
                    let indx = cart.findIndex((e, i) => {
                        return e.id === cart[i].id
                    })
                    cart.splice(indx, 1)
                    this.setState({
                        status: 'Delete ' + posRes.statusText
                    })
                }, (errRes) => {
                    console.log(errRes)
                    this.setState({
                        status: errRes.message
                    })
                })
        }
    }

    reduce = (item) => {
        console.log('Item id:- ', item.id)
        if (item.qty == 1) {
            let obj = {
                "u_name": this.state.user,
                "p_id": item.p_id
            }
            axios.post(url + "/delete/deletefromCart", obj)
                .then((posRes) => {
                    console.log(posRes)
                    let indx = cart.findIndex((e, i) => {
                        return e.p_id === item.p_id
                    })
                    cart.splice(indx, 1)
                    this.setState({
                        status: 'Delete ' + posRes.statusText
                    })
                }, (errRes) => {
                    console.log(errRes)
                    this.setState({
                        status: errRes.message
                    })
                })
        }
        else {
            let obj = {
                "u_name": this.state.user,
                "p_id": item.p_id,
                "qty": parseInt(item.qty) - 1,
            }
            axios.post(url + "/update/updateCart", obj)
                .then((posRes) => {
                    cart.forEach((e, i) => {
                        if (e.p_id == obj.p_id)
                            e.qty = obj.qty
                    })
                    console.log(posRes.statusText)
                    this.setState({
                        status: 'Update ' + posRes.statusText
                    })
                }, (errRes) => {
                    console.log(errRes)
                    this.setState({
                        status: errRes.message
                    })
                })
        }
        this.setState({
            total: this.calculateTotal()
        })
    }

    fetchCart = () => {
        axios.post(url + "/fetch/fetchCart", { "uname": window.sessionStorage.getItem('user') }).then((posRes) => {
            this.setState({
                status: 'Loading'
            })
            cart = posRes.data
            console.log('Cart data:- ', cart)
            this.setState({
                status: '',
                user: window.sessionStorage.getItem('user') // Set user in the state
            })
        }, (errRes) => {
            console.log(errRes)
        })
        let total = 0
        for (let i = 0; i < cart.length; i++) {
            debugger
            total += cart[i].p_cost * cart[i].qty
        }
        this.setState({
            total: total
        })
    }

    calculateTotal = () => {
        let total = 0
        cart.forEach((e, i) => {
            total += e.qty * e.p_cost
        })
        return total
    }

    login = (e) => {
        e.preventDefault()
        let obj = {
            u_name: e.target.u_name.value,
            u_pwd: e.target.u_pwd.value
        }
        axios.post(url + "/fetch/login", obj)
            .then((posRes) => {
                console.log(posRes.data)
                if (posRes.data.auth == 'success') {
                    console.log('Object in auth :- ', obj)
                    this.setState({
                        ...this.state,
                        login: true,
                        user: obj.u_name // Set user in the state
                    })
                    console.log('State after login:- ', this.state)
                    window.sessionStorage.setItem('user', obj.u_name)
                }
            }, (errRes) => {
                console.log(errRes)
            })
    }

    logout = () => {
        this.setState({
            login: false,
            user: '' // Reset user in the state
        })
        window.sessionStorage.removeItem('user')
    }
}
